package main

import "testing"

func TestBathroomCode(t *testing.T) {
	testCases := []struct {
		complex bool
		cmd     string
		code    string
	}{
		{false, `ULL
RRDDD
LURDL
UUUUD`, "1985"},
		{false, `



`, "56983"},
		{true, `ULL
RRDDD
LURDL
UUUUD`, "5DB3"},
		{true, `



`, "8B8B1"},
	}

	for _, tt := range testCases {
		c := BathroomCode(tt.cmd, tt.complex)
		if tt.code != c {
			t.Errorf("BathroomCode(%s) %s != %s", tt.cmd, tt.code, c)
		}
	}
}
