package main

import "testing"

func TestBathroomCode(t *testing.T) {
	testCases := []struct {
		cmd  string
		code string
	}{
		{`ULL
RRDDD
LURDL
UUUUD`, "1985"},
		{`



`, "56983"},
	}

	for _, tt := range testCases {
		c := BathroomCode(tt.cmd)
		if tt.code != c {
			t.Errorf("BathroomCode(%s) %s != %s", tt.cmd, tt.code, c)
		}
	}
}
